import React from 'react';

class FloatingButton extends React.Component {
    constructor(props) {
      super (props);
      this.state = {menuOpened: this.props.menuOpened}
    }
    render() {
      alert(this.state.menuOpened)
      const buttonDisable = this.props.menuOpened == true;
      return(
        <div className="floatbtn" onClick={this.props.handleClick} disabled={buttonDisable}>
          <span className="floatbtn-icon fa fa-plus"></span>
        </div>  
      );
    }
}

export default FloatingButton;
